package week1.day2;

public class OddOrEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=15;
		
		if(num%2==0) {
			System.out.println("The given no."+num+ " is an even number");
		}else
			System.out.println("The given no."+num+ " is an odd number");
			
	}

}
